import React, { useState } from 'react';

function AggregationForm() {
  const [uai, setUai] = useState('');
  const [granularity, setGranularity] = useState('year');
  const [results, setResults] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Effectuez ici la logique d'agrégation des données en fonction de l'UAI et de la granularité
    // Mettez les résultats à jour avec setResults

    // Exemple de mise à jour des résultats
    const dummyResults = [
      { id: 1, value: 'Résultat 1' },
      { id: 2, value: 'Résultat 2' },
      { id: 3, value: 'Résultat 3' },
    ];
    setResults(dummyResults);
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-white shadow-md rounded-md">
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="uai" className="block text-gray-700">
            UAI:
          </label>
          <input
            type="text"
            id="uai"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
          />
        </div>
        <div>
          <label htmlFor="granularity" className="block text-gray-700">
            Granularité:
          </label>
          <select
            id="granularity"
            value={granularity}
            onChange={(e) => setGranularity(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="year">Année</option>
            <option value="month">Mois</option>
            <option value="week">Semaine</option>
          </select>
        </div>
        <button
          type="submit"
          className="w-full py-2 px-4 bg-blue-500 text-white rounded-md hover:bg-blue-600"
        >
          Soumettre
        </button>
      </form>

      <table className="w-full mt-4">
        <thead>
          <tr>
            <th className="py-2 px-4 border-b border-gray-300">ID</th>
            <th className="py-2 px-4 border-b border-gray-300">Valeur</th>
          </tr>
        </thead>
        <tbody>
          {results.map((result) => (
            <tr key={result.id}>
              <td className="py-2 px-4 border-b border-gray-300">{result.id}</td>
              <td className="py-2 px-4 border-b border-gray-300">{result.value}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AggregationForm;
