import React, { useState } from 'react';

function GraphForm() {
  const [uai, setUai] = useState('');
  const [year, setYear] = useState('');
  const [graphData, setGraphData] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Effectuez ici la logique pour récupérer les données d'évolution par mois selon l'UAI et l'année
    // Mettez à jour les données du graphique avec setGraphData

    // Exemple de mise à jour des données du graphique
    const dummyData = [
      { month: 'Jan', tablet: 10, smartphone: 15, computer: 20 },
      { month: 'Feb', tablet: 12, smartphone: 18, computer: 22 },
      { month: 'Mar', tablet: 8, smartphone: 14, computer: 18 },
    ];
    setGraphData(dummyData);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          UAI:
          <input type="text" value={uai} onChange={(e) => setUai(e.target.value)} />
        </label>
        <br />
        <label>
          Année:
          <input type="text" value={year} onChange={(e) => setYear(e.target.value)} />
        </label>
        <br />
        <button type="submit">Soumettre</button>
      </form>

      {/* Utilisez une bibliothèque de graphiques comme Chart.js pour afficher les données */}
    </div>
  );
}

export default GraphForm;
