import React, { useState } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import 'chart.js/auto';

const DataComponent = () => {
  const [uai, setUai] = useState("");
  const [year, setYear] = useState("");
  const [chartData, setChartData] = useState(null);
  const [error, setError] = useState(null);

  const handleChartSubmit = (e) => {
    e.preventDefault();

    axios
      .get(
        `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=&facet=debutsemaine${year}&facet=uai&refine.uai=${uai}`
      )
      .then((response) => {
        const records = response.data.records;
        const data = processDataForChart(records);
        setChartData(data);
        setError(null);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setChartData(null);
        setError("Error fetching data. Please try again.");
      });
  };

  const processDataForChart = (records) => {
    const months = [
      "Jan",
      "Feb",
      "Mar",
      "Apr",
      "May",
      "Jun",
      "Jul",
      "Aug",
      "Sep",
      "Oct",
      "Nov",
      "Dec",
    ];

    // ...

    return data;
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <form onSubmit={handleChartSubmit} className="max-w-lg mx-auto">
        <label className="block mb-4">
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
            className="block w-full border border-gray-300 rounded py-2 px-4 mt-2 focus:outline-none focus:border-indigo-500"
          />
        </label>
        <label className="block mb-4">
          Year:
          <input
            type="text"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            className="block w-full border border-gray-300 rounded py-2 px-4 mt-2 focus:outline-none focus:border-indigo-500"
          />
        </label>
        <button
          type="submit"
          className="bg-indigo-500 text-white rounded py-2 px-4 hover:bg-indigo-700 focus:outline-none focus:bg-indigo-700"
        >
          Generate Chart
        </button>
      </form>

      {chartData ? (
        <div>
          <h2 className="text-2xl font-bold mt-8">Visits by Month</h2>
          <Line data={chartData} />
        </div>
      ) : (
        <p className="text-red-500 mt-8">{error}</p>
      )}
    </div>
  );
};

export default DataComponent;
