import React, { useState } from 'react';
import axios from 'axios';

const DataComponent = () => {
  const [uai, setUai] = useState('');
  const [granularity, setGranularity] = useState('Année');
  const [results, setResults] = useState([]);

  const handleDataSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.get(
        `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=&facet=debutsemaine&facet=uai&refine.uai=${uai}&granularity=${granularity}`
      );
      setResults(response.data.records);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div>
      <h2>Data Component</h2>
      <form onSubmit={handleDataSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>
        <br />
        <label>
          Granularité:
          <select
            value={granularity}
            onChange={(e) => setGranularity(e.target.value)}
          >
            <option value="Année">Année</option>
            <option value="Mois">Mois</option>
            <option value="Semaine">Semaine</option>
          </select>
        </label>
        <br />
        <button type="submit">Rechercher</button>
      </form>
      {results.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Date de début de la semaine</th>
              <th>UAI</th>
              <th>Visites - Tablette</th>
              <th>Visites - Smartphone</th>
              <th>Visites - Ordinateur</th>
            </tr>
          </thead>
          <tbody>
            {results.map((record) => (
              <tr key={record.recordid}>
                <td>{record.fields.debutsemaine}</td>
                <td>{record.fields.uai}</td>
                <td>{record.fields.visites_tablette}</td>
                <td>{record.fields.visites_smartphone}</td>
                <td>{record.fields.visites_ordinateur}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default DataComponent;
