import React, { useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';


const DataComponent = () => {
  const [uai, setUai] = useState('');
  const [granularity, setGranularity] = useState('Année');
  const [results, setResults] = useState([]);
  const [year, setYear] = useState('');
  const [chartData, setChartData] = useState({});

  const handleDataSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.get(
        `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=&facet=debutsemaine&facet=uai&refine.uai=${uai}`
      );
      setResults(response.data.records);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleChartSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.get(
        `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=&facet=debutsemaine&facet=uai&refine.uai=${uai}&year=${year}`
      );
      const chartData = processDataForChart(response.data.records);
      setChartData(chartData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const processDataForChart = (data) => {
    const monthlyData = data.reduce((acc, record) => {
      const month = record.fields.debutsemaine.slice(0, 7);
      const deviceType = record.fields.appareil;
      const visits = record.fields.visites;

      if (!acc[month]) {
        acc[month] = {
          month: month,
          tablet: 0,
          smartphone: 0,
          ordinateur: 0,
        };
      }

      if (deviceType === 'Tablette') {
        acc[month].tablet += visits;
      } else if (deviceType === 'Smartphone') {
        acc[month].smartphone += visits;
      } else if (deviceType === 'Ordinateur') {
        acc[month].ordinateur += visits;
      }

      return acc;
    }, {});

    const chartData = {
      labels: Object.keys(monthlyData),
      datasets: [
        {
          label: 'Tablette',
          data: Object.values(monthlyData).map((data) => data.tablet),
          borderColor: 'red',
          backgroundColor: 'red',
          fill: false,
        },
        {
          label: 'Smartphone',
          data: Object.values(monthlyData).map((data) => data.smartphone),
          borderColor: 'blue',
          backgroundColor: 'blue',
          fill: false,
        },
        {
          label: 'Ordinateur',
          data: Object.values(monthlyData).map((data) => data.ordinateur),
          borderColor: 'green',
          backgroundColor: 'green',
          fill: false,
        },
      ],
    };

    return chartData;
  };

  return (
    <div>
      <h2>Data Component</h2>
      <form onSubmit={handleDataSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>
        <br />
        <label>
          Granularité:
          <select
            value={granularity}
            onChange={(e) => setGranularity(e.target.value)}
          >
            <option value="Année">Année</option>
            <option value="Mois">Mois</option>
            <option value="Semaine">Semaine</option>
          </select>
        </label>
        <br />
        <button type="submit">Rechercher</button>
      </form>
      {results.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Date de début de la semaine</th>
              <th>UAI</th>
              <th>Visites - Tablette</th>
              <th>Visites - Smartphone</th>
              <th>Visites - Ordinateur</th>
            </tr>
          </thead>
          <tbody>
            {results.map((record) => (
              <tr key={record.recordid}>
                <td>{record.fields.debutsemaine}</td>
                <td>{record.fields.uai}</td>
                <td>{record.fields.visites_tablette}</td>
                <td>{record.fields.visites_smartphone}</td>
                <td>{record.fields.visites_ordinateur}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <h2>Graph Component</h2>
      <form onSubmit={handleChartSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>
        <br />
        <label>
          Année:
          <input
            type="text"
            value={year}
            onChange={(e) => setYear(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Rechercher</button>
      </form>
      {Object.keys(chartData).length > 0 && (
        <Line data={chartData} />
      )}
    </div>
  );
};

export default DataComponent;
