import React, { useState } from "react";
import axios from "axios";

const DataComponent = () => {
  const [uai, setUai] = useState("");
  const [granularity, setGranularity] = useState("");
  const [tableData, setTableData] = useState([]);
  const [error, setError] = useState(null);

  const handleTableSubmit = (e) => {
    e.preventDefault();

    axios
      .get(
        `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=&facet=debutsemaine${granularity}&facet=uai&refine.uai=${uai}`
      )
      .then((response) => {
        const records = response.data.records;
        const data = processDataForTable(records);
        setTableData(data);
        setError(null);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setTableData([]);
        setError("Error fetching data. Please try again.");
      });
  };

  const processDataForTable = (records) => {
    let tableData = [];

    records.forEach((record) => {
      const week = record.fields.debutsemaine;
      const tabletVisits = record.fields.visites_tablette || 0;
      const smartphoneVisits = record.fields.visites_smartphone || 0;
      const computerVisits = record.fields.visites_ordinateur || 0;

      tableData.push({
        Week: week,
        TabletVisits: tabletVisits,
        SmartphoneVisits: smartphoneVisits,
        ComputerVisits: computerVisits,
      });
    });

    return tableData;
  };

  return (
    <div>
      <form onSubmit={handleTableSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>
        <br />
        <label>
          Granularity:
          <select
            value={granularity}
            onChange={(e) => setGranularity(e.target.value)}
          >
            <option value="">Select Granularity</option>
            <option value="Month">Month</option>
            <option value="Week">Week</option>
            <option value="Year">Year</option>
          </select>
        </label>
        <br />
        <button type="submit">Generate Table</button>
      </form>

      {tableData.length > 0 ? (
        <div>
          <h2>Visits Table</h2>
          <table>
            <thead>
              <tr>
                <th>Week</th>
                <th>Tablet Visits</th>
                <th>Smartphone Visits</th>
                <th>Computer Visits</th>
              </tr>
            </thead>
            <tbody>
              {tableData.map((dataRow, index) => (
                <tr key={index}>
                  <td>{dataRow.Week}</td>
                  <td>{dataRow.TabletVisits}</td>
                  <td>{dataRow.SmartphoneVisits}</td>
                  <td>{dataRow.ComputerVisits}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>{error}</p>
      )}
    </div>
  );
};

export default DataComponent;
