import React, { useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

const DataComponent = () => {
  const [uai, setUai] = useState('');
  const [year, setYear] = useState('');
  const [chartData, setChartData] = useState({});

  const handleChartSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.get(
        `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=&facet=debutsemaine${year}&facet=uai&refine.uai=${uai}`
       
      );
      const chartData = processDataForChart(response.data.records);
      setChartData(chartData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const processDataForChart = (data) => {
    const monthlyData = data.reduce((acc, record) => {
      const month = record.fields.debutsemaine.slice(0, 7);
      const deviceType = record.fields.appareil;
      const visits = record.fields[`visites_${deviceType.toLowerCase()}`];
  
      if (!acc[month]) {
        acc[month] = {
          month: month,
          tablet: 0,
          smartphone: 0,
          ordinateur: 0,
        };
      }
  
      acc[month][deviceType.toLowerCase()] += visits;
  
      return acc;
    }, {});
  
    const chartData = {
      labels: Object.keys(monthlyData),
      datasets: [
        {
          label: 'Tablette',
          data: Object.values(monthlyData).map((data) => data.tablet),
          borderColor: 'red',
          backgroundColor: 'red',
          fill: false,
        },
        {
          label: 'Smartphone',
          data: Object.values(monthlyData).map((data) => data.smartphone),
          borderColor: 'blue',
          backgroundColor: 'blue',
          fill: false,
        },
        {
          label: 'Ordinateur',
          data: Object.values(monthlyData).map((data) => data.ordinateur),
          borderColor: 'green',
          backgroundColor: 'green',
          fill: false,
        },
      ],
    };
  
    return chartData;
  };
  
  

  return (
    <div>
      <h2>Graph Component</h2>
      <form onSubmit={handleChartSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>
        <br />
        <label>
          Ann√©e:
          <input
            type="text"
            value={year}
            onChange={(e) => setYear(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Rechercher</button>
      </form>
      {Object.keys(chartData).length > 0 && <Line data={chartData} />}
    </div>
  );
};

export default DataComponent;
