import React, { useState } from 'react';

function App() {
  const [uai, setUai] = useState('');
  const [granularity, setGranularity] = useState('Année');
  const [data, setData] = useState([]);

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    // Effectuer une requête à l'API en utilisant les valeurs saisies
    const response = await fetch(
      `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=debut${granularity.toLowerCase()}%3D%27-01-01%27&facet=debut${granularity.toLowerCase()}&facet=uai&refine.uai=${uai}`
    );
    const result = await response.json();

    // Extraire les données agrégées du résultat de l'API
    const aggregatedData = result.records.map((record) => record.fields);

    setData(aggregatedData);
  };

  return (
    <div>
      <form onSubmit={handleFormSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>

        <label>
          Granularité:
          <select
            value={granularity}
            onChange={(e) => setGranularity(e.target.value)}
          >
            <option value="Année">Année</option>
            <option value="Mois">Mois</option>
            <option value="Semaine">Semaine</option>
          </select>
        </label>

        <button type="submit">Rechercher</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>UAI</th>
            <th>Visites Smartphone</th>
            <th>Visites Tablette</th>
            <th>Visites Ordinateur</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.uai}</td>
              <td>{item.visites_smartphone}</td>
              <td>{item.visites_tablette}</td>
              <td>{item.visites_ordinateur}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
