import React, { useState } from 'react';

function AggregationForm() {
  const [uai, setUai] = useState('');
  const [granularity, setGranularity] = useState('year');
  const [results, setResults] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Effectuez ici la logique d'agrégation des données en fonction de l'UAI et de la granularité
    // Mettez les résultats à jour avec setResults

    // Exemple de mise à jour des résultats
    const dummyResults = [
      { id: 1, value: 'Result 1' },
      { id: 2, value: 'Result 2' },
      { id: 3, value: 'Result 3' },
    ];
    setResults(dummyResults);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          UAI:
          <input type="text" value={uai} onChange={(e) => setUai(e.target.value)} />
        </label>
        <br />
        <label>
          Granularité:
          <select value={granularity} onChange={(e) => setGranularity(e.target.value)}>
            <option value="year">Année</option>
            <option value="month">Mois</option>
            <option value="week">Semaine</option>
          </select>
        </label>
        <br />
        <button type="submit">Soumettre</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Valeur</th>
          </tr>
        </thead>
        <tbody>
          {results.map((result) => (
            <tr key={result.id}>
              <td>{result.id}</td>
              <td>{result.value}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AggregationForm;
