const processDataForChart = (data) => {
    const monthlyData = data.reduce((acc, record) => {
      const month = record.fields.debutsemaine.slice(0, 7);
      const deviceType = record.fields.appareil;
      const visits = record.fields[`visites_${deviceType.toLowerCase()}`];
  
      if (!acc[month]) {
        acc[month] = {
          month: month,
          tablet: 0,
          smartphone: 0,
          ordinateur: 0,
        };
      }
  
      acc[month][deviceType.toLowerCase()] += visits;
  
      return acc;
    }, {});
  
    const chartData = {
      labels: Object.keys(monthlyData),
      datasets: [
        {
          label: 'Tablette',
          data: Object.values(monthlyData).map((data) => data.tablet),
          borderColor: 'red',
          backgroundColor: 'red',
          fill: false,
        },
        {
          label: 'Smartphone',
          data: Object.values(monthlyData).map((data) => data.smartphone),
          borderColor: 'blue',
          backgroundColor: 'blue',
          fill: false,
        },
        {
          label: 'Ordinateur',
          data: Object.values(monthlyData).map((data) => data.ordinateur),
          borderColor: 'green',
          backgroundColor: 'green',
          fill: false,
        },
      ],
    };
  
    return chartData;
  };
