import React, { useState } from 'react';
import axios from 'axios';

const DataComponent = () => {
  const [uai, setUai] = useState('');
  const [granularity, setGranularity] = useState('Année');
  const [results, setResults] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.get(
        `https://data.education.gouv.fr/api/records/1.0/search/?dataset=your-dataset&refine.uai=${uai}&granularity=${granularity}`
      );
      setResults(response.data.records);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div>
      <h2>Data Component</h2>
      <form onSubmit={handleSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>
        <br />
        <label>
          Granularité:
          <select value={granularity} onChange={(e) => setGranularity(e.target.value)}>
            <option value="Année">Année</option>
            <option value="Mois">Mois</option>
            <option value="Semaine">Semaine</option>
          </select>
        </label>
        <br />
        <button type="submit">Rechercher</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>Champ 1</th>
            <th>Champ 2</th>
            {/* Ajoutez les en-têtes des colonnes supplémentaires ici */}
          </tr>
        </thead>
        <tbody>
          {results.map((record) => (
            <tr key={record.recordid}>
              <td>{record.fields.champ1}</td>
              <td>{record.fields.champ2}</td>
              {/* Ajoutez les cellules des colonnes supplémentaires ici */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DataComponent;
