import React, { useState } from 'react';
import axios from 'axios';

const DataComponent = () => {
  const [uai, setUai] = useState('');
  const [granularity, setGranularity] = useState('Année');
  const [results, setResults] = useState([]);
  const [filteredResults, setFilteredResults] = useState([]);

  const handleDataSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.get(
        `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=&facet=debutsemaine&facet=uai&refine.uai=${uai}`
      );
      setResults(response.data.records);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleGranularityFilter = () => {
    let filteredData = [];

    if (granularity === 'Année') {
      // Filtrer les données par année
      const years = [...new Set(results.map((record) => record.fields.debutsemaine.slice(0, 4)))];
      filteredData = results.filter((record) => years.includes(record.fields.debutsemaine.slice(0, 4)));
    } else if (granularity === 'Mois') {
      // Filtrer les données par mois
      const months = [...new Set(results.map((record) => record.fields.debutsemaine.slice(0, 7)))];
      filteredData = results.filter((record) => months.includes(record.fields.debutsemaine.slice(0, 7)));
    } else if (granularity === 'Semaine') {
      // Filtrer les données par semaine
      filteredData = results;
    }

    setFilteredResults(filteredData);
  };

  return (
    <div>
      <h2>Data Component</h2>
      <form onSubmit={handleDataSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>
        <br />
        <label>
          Granularité:
          <select
            value={granularity}
            onChange={(e) => setGranularity(e.target.value)}
          >
            <option value="Année">Année</option>
            <option value="Mois">Mois</option>
            <option value="Semaine">Semaine</option>
          </select>
        </label>
        <br />
        <button type="submit">Rechercher</button>
      </form>
      {filteredResults.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Date de début de la semaine</th>
              <th>UAI</th>
              <th>Visites - Tablette</th>
              <th>Visites - Smartphone</th>
              <th>Visites - Ordinateur</th>
            </tr>
          </thead>
          <tbody>
            {filteredResults.map((record) => (
              <tr key={record.recordid}>
                <td>{record.fields.debutsemaine}</td>
                <td>{record.fields.uai}</td>
                <td>{record.fields.visites_tablette}</td>
                <td>{record.fields.visites_smartphone}</td>
                <td>{record.fields.visites_ordinateur}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default DataComponent;
