import React, { useState } from 'react';

function App() {
  const [uai, setUai] = useState('');
  const [data, setData] = useState([]);

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    // Effectuer une requête à l'API en utilisant la valeur saisie pour l'UAI
    const response = await fetch(
      `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=uai%3D%27${uai}%27`
    );
    const result = await response.json();

    // Extraire les données du résultat de l'API
    const extractedData = result.records?.map((record) => record.fields) || [];

    setData(extractedData);
  };

  return (
    <div>
      <form onSubmit={handleFormSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>
        <button type="submit">Afficher les données</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>UAI</th>
            {/* Ajouter les autres en-têtes de colonnes en fonction des attributs souhaités */}
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.uai}</td>
              {/* Ajouter les autres cellules de données en fonction des attributs souhaités */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
