import React, { useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';

const DataComponent = () => {
  const [uai, setUai] = useState('');
  const [granularity, setGranularity] = useState('AnnÃ©e');
  const [results, setResults] = useState([]);
  const [year, setYear] = useState('');
  const [chartData, setChartData] = useState({});
  const [chartOptions, setChartOptions] = useState({});

  const handleDataSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.get(
        `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=&facet=debutsemaine&facet=uai&refine.uai=${uai}`
      );
      setResults(response.data.records);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleChartSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.get(
        `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=&facet=debutsemaine&facet=uai&refine.uai=${uai}&year=${year}`
      );
      const { chartData, chartOptions } = processDataForChart(response.data.records);
      setChartData(chartData);
      setChartOptions(chartOptions);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const processDataForChart = (data) => {
    const monthlyData = data.reduce((acc, record) => {
      const month = record.fields.debutsemaine.slice(0, 7);
      const deviceType = record.fields.appareil;
      const visits = record.fields.visites;

      if (!acc[month]) {
        acc[month] = {
          month: month,
          tablet: 0,
          smartphone: 0,
          ordinateur: 0,
        };
      }

      acc[month][deviceType] += visits;

      return acc;
    }, {});

    const chartData = {
      labels: Object.keys(monthlyData),
      datasets: [
        {
          label: 'Tablette',
          data: Object.values(monthlyData).map((data) => data.tablet),
          borderColor: 'red',
          backgroundColor: 'red',
          fill: false,
        },
        {
          label: 'Smartphone',
          data: Object.values(monthlyData).map((data) => data.smartphone),
          borderColor: 'blue',
          backgroundColor: 'blue',
          fill: false,
        },
        {
          label: 'Ordinateur',
          data: Object.values(monthlyData).map((data) => data.ordinateur),
          borderColor: 'green',
          backgroundColor: 'green',
          fill: false,
        },
      ],
    };

    const chartOptions = {
      scales: {
        x: {
          type: 'category',
          title: {
            display: true,
            text: 'Mois',
          },
        },
        y: {
          type: 'linear',
          title: {
            display: true,
            text: 'Nombre de visites',
          },
        },
      },
    };

    return { chartData, chartOptions };
  };

  return (
    <div>
      <h2>Data Component</h2>
      <form onSubmit={handleDataSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>
        <br />
        <label>
          GranularitÃ©:
          <select
            value={granularity}
            onChange={(e) => setGranularity(e.target.value)}
          >
            <option value="AnnÃ©e">AnnÃ©e</option>
            <option value="Mois">Mois</option>
            <option value="Semaine">Semaine</option>
          </select>
        </label>
        <br />
        <button type="submit">Rechercher</button>
      </form>
      {results.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Date de dÃ©but de la semaine</th>
              <th>UAI</th>
              {/* Ajoutez les en-tÃªtes des colonnes supplÃ©mentaires ici */}
            </tr>
          </thead>
          <tbody>
            {results.map((record) => (
              <tr key={record.recordid}>
                <td>{record.fields.debutsemaine}</td>
                <td>{record.fields.uai}</td>
                {/* Ajoutez les cellules des colonnes supplÃ©mentaires ici */}
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <h2>Graph Component</h2>
      <form onSubmit={handleChartSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>
        <br />
        <label>
          AnnÃ©e:
          <input
            type="text"
            value={year}
            onChange={(e) => setYear(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Rechercher</button>
      </form>
      {Object.keys(chartData).length > 0 && (
        <Line data={chartData} options={chartOptions} />
      )}
    </div>
  );
};

export default DataComponent;
