import React, { useState } from "react";
import axios from "axios";

const FormComponent = () => {
  const [uai, setUai] = useState("");
  const [chartData, setChartData] = useState(null);
  const [error, setError] = useState(null);

  const handleFormSubmit = (e) => {
    e.preventDefault();

    axios
      .get(
        `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=&facet=debutsemaine&facet=uai&refine.uai=${uai}`
      )
      .then((response) => {
        const records = response.data.records;
        const data = processData(records);
        setChartData(data);
        setError(null);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setChartData(null);
        setError("Error fetching data. Please try again.");
      });
  };

  const processData = (records) => {
    const aggregatedData = {};

    records.forEach((record) => {
      const weekStartDate = record.fields.debutsemaine;
      const month = weekStartDate.slice(5, 7); // Extract the month from the start date
      const week = weekStartDate.slice(8, 10); // Extract the week from the start date

      if (!aggregatedData[month]) {
        aggregatedData[month] = {};
      }

      if (!aggregatedData[month][week]) {
        aggregatedData[month][week] = {
          visits_tablette: 0,
          visits_smartphone: 0,
          visits_ordinateur: 0,
        };
      }

      aggregatedData[month][week].visits_tablette += record.fields.visites_tablette || 0;
      aggregatedData[month][week].visits_smartphone += record.fields.visites_smartphone || 0;
      aggregatedData[month][week].visits_ordinateur += record.fields.visites_ordinateur || 0;
    });

    return aggregatedData;
  };

  return (
    <div>
      <form onSubmit={handleFormSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Generate Table</button>
      </form>

      {chartData ? (
        <div>
          <h2>Data Table</h2>
          <table>
            <thead>
              <tr>
                <th>Month</th>
                <th>Week</th>
                <th>Visits Tablet</th>
                <th>Visits Smartphone</th>
                <th>Visits Computer</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(chartData).map((month) =>
                Object.keys(chartData[month]).map((week) => (
                  <tr key={`${month}-${week}`}>
                    <td>{month}</td>
                    <td>{week}</td>
                    <td>{chartData[month][week].visits_tablette}</td>
                    <td>{chartData[month][week].visits_smartphone}</td>
                    <td>{chartData[month][week].visits_ordinateur}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      ) : (
        <p>{error}</p>
      )}
    </div>
  );
};

export default FormComponent;
