import React, { useState } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import "chart.js/auto";

const DataComponent = () => {
  const [uai, setUai] = useState("");
  const [year, setYear] = useState("");
  const [granularity, setGranularity] = useState("");
  const [chartData, setChartData] = useState(null);
  const [error, setError] = useState(null);

  const handleChartSubmit = (e) => {
    e.preventDefault();

    axios
      .get(
        `https://data.education.gouv.fr/api/records/1.0/search/?dataset=fr-en-dnma-par-uai-appareils&q=&facet=debutsemaine${granularity}&facet=uai&refine.uai=${uai}`
      )
      .then((response) => {
        const records = response.data.records;
        const data = processDataForChart(records);
        setChartData(data);
        setError(null);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setChartData(null);
        setError("Error fetching data. Please try again.");
      });
  };

  const processDataForChart = (records) => {
    let data;
    if (granularity === "Month") {
      data = aggregateByMonth(records);
    } else if (granularity === "Week") {
      data = aggregateByWeek(records);
    } else if (granularity === "Year") {
      data = aggregateByYear(records);
    } else {
      return null;
    }
    return data;
  };

  const aggregateByMonth = (records) => {
    const months = [
      "Jan",
      "Feb",
      "Mar",
      "Apr",
      "May",
      "Jun",
      "Jul",
      "Aug",
      "Sep",
      "Oct",
      "Nov",
      "Dec",
    ];

    const visitsTablet = Array(12).fill(0);
    const visitsSmartphone = Array(12).fill(0);
    const visitsComputer = Array(12).fill(0);

    records.forEach((record) => {
      const monthIndex = new Date(record.fields.debutsemaine).getMonth();
      visitsTablet[monthIndex] += record.fields.visites_tablette || 0;
      visitsSmartphone[monthIndex] += record.fields.visites_smartphone || 0;
      visitsComputer[monthIndex] += record.fields.visites_ordinateur || 0;
    });

    data = {
      labels: months,
      datasets: [
        {
          label: "Tablet",
          data: visitsTablet,
          backgroundColor: "rgba(255, 99, 132, 0.2)",
          borderColor: "rgba(255, 99, 132, 1)",
          borderWidth: 1,
        },
        {
          label: "Smartphone",
          data: visitsSmartphone,
          backgroundColor: "rgba(54, 162, 235, 0.2)",
          borderColor: "rgba(54, 162, 235, 1)",
          borderWidth: 1,
        },
        {
          label: "Computer",
          data: visitsComputer,
          backgroundColor: "rgba(75, 192, 192, 0.2)",
          borderColor: "rgba(75, 192, 192, 1)",
          borderWidth: 1,
        },
      ],
    };

    return data;
  };

  const aggregateByWeek = (records) => {
    // Agrégation des données par semaine
    // ...
    return data;
  };

  const aggregateByYear = (records) => {
    // Agrégation des données par année
    // ...
    return data;
  };

  return (
    <div>
      <form onSubmit={handleChartSubmit}>
        <label>
          UAI:
          <input
            type="text"
            value={uai}
            onChange={(e) => setUai(e.target.value)}
          />
        </label>
        <br />
        <label>
          Year:
          <input
            type="text"
            value={year}
            onChange={(e) => setYear(e.target.value)}
          />
        </label>
        <br />
        <label>
          Granularity:
          <select
            value={granularity}
            onChange={(e) => setGranularity(e.target.value)}
          >
            <option value="">Select Granularity</option>
            <option value="Month">Month</option>
            <option value="Week">Week</option>
            <option value="Year">Year</option>
          </select>
        </label>
        <br />
        <button type="submit">Generate Chart</button>
      </form>

      {chartData ? (
        <div>
          <h2>Visits by {granularity}</h2>
          <Line data={chartData} />
        </div>
      ) : (
        <p>{error}</p>
      )}
    </div>
  );
};

export default DataComponent;
